export default function SinglePost() {
    const location = useLocation();
    const path = location.pathname.split("/")[2];
  return (
    <div className="singlepost">
        <div className="singlePostWrapper">
            <img src="" alt="" className="singlePostImg" />
            <h1 className="singlePostTitle">lorem ipsum dolor
            <div className="singlePostEdit">
                <i className="singlePostIcon far fa-edit"></i>
                <i className="singlePostIcon far fa-trash-alt"></i>
            </div>
            </h1>
            <div className="singlePostInfo">
                <span className="singlePostAuthor">Employee: <b>XYz</b></span>
                <span className="singlePostDate">1 hour ago</span>
            </div>
            <p className="singlePostDesc">
                Lorem ipsum, dolor sit amet consectetur adipisicing elit. Earum, veniam consequatur! Quod corporis minus distinctio culpa, nam fugit fuga cum, quos reprehenderit consequuntur a nobis suscipit accusamus necessitatibus porro sapiente.
            </p>
        </div>
    </div>
  )
}
